import React from 'react';
import axios from 'axios';

import { MovieCard } from '../movie-card/movie-card';
import { MovieView } from '../movie-view/movie-view';

export class MainView extends React.Component {
  
  constructor(){
    super();
    this.state = {
      movies: [
        { _id:1, Title:'Inception', Genre:'Sci-Fi', Director:'Christopher Nolan', Description: 'A thief who steals corporate secrets through the use of dream-sharing technology is given the inverse task of planting an idea into the mind of a C.E.O.', ImagePath: 'data:image/jpeg;base64,'},
        { _id:2, Title:'The Shawshank Redemption', Genre:'Drama', Director:'Frank Darabont', Description: 'Two imprisoned men bond over a number of years, finding solace and eventual redemption through acts of common decency.', ImagePath: 'data:image/jpeg;base64,'},
        { _id:3, Title:'Gladiator', Genre:'Adventure', Director:'Ridley Scott', Description: 'A former Roman General sets out to exact vengeance against the corrupt emperor who murdered his family and sent him into slavery.', ImagePath: 'data:image/jpeg;base64,'},
      ],
      selectedMovie: null
    };
  }
  
  setSelectedMovie(newSelectedMovie) {
    this.setState({
      selectedMovie: newSelectedMovie
    });
  }

  render() {
    const {movies, selectedMovie } = this.state;

    // if (selectedMovie) return <MovieView movie={selectedMovie} />

    if (movies.length === 0) return <div className="main-view">The list is empty!</div>;

    return (
      <div className="main-view">
        {selectedMovie
          ? <MovieView movie={selectedMovie} onBackClick={newSelectedMovie => { this.setSelectedMovie(newSelectedMovie); }}/>
          : movies.map(movie => (
            <MovieCard key={movie._id} movie={movie} onMovieClick={(movie) => {this.setSelectedMovie(movie) }}/>
          ))
        
        /* {movies.map(movie => <MovieCard key={movie._id} movie ={movie} onMovieClick={newSelectedMovie => { this.setSelectedMovie(movie) }} />)} */}
      </div>
      );
  }
}